/**
 * Created by TinySymphony on 2017-01-03.
 */

import PropTypes from 'prop-types';
import React, { useReducer } from 'react';
import { View, Text, Image, Modal, ScrollView, TouchableHighlight, Dimensions, StyleSheet } from 'react-native';
import ModalItem from './pTransparentModal';
import PModal from './pTransparentModal';
import wordsReducer from '../store/reducers/wordsReducer'
import { useSelector } from 'react-redux';
const window = Dimensions.get('window');
const { width, height, scale } = window;

function LabelSelect(props: any) {
    const addIcon = {uri: 'data:image/png;base64,'}
    const words = useSelector((state:any) => state.words.data)
    const openModal = () => {

    }
    const createTable = () => {
        console.log(words)
    return 
    }
    return (
        <View style={[styles.selectedView, props.style]}>
          {words.map((word:any)=>(<WordLabel key={word.title}  enable>{word.title}</WordLabel>))}

            {props.enable && !props.readOnly && props.enableAddBtn &&
                <TouchableHighlight
                    style={[styles.selectedItem, styles.addItem]}
                    underlayColor="transparent"
                    onPress={openModal}
                    >
                    <Image
                        style={styles.addIcon}
                        source={addIcon}
                        resizeMode="cover"
                    />
                </TouchableHighlight>
            }
            <PModal ></PModal>
        </View>
    );
}

function WordLabel(props:any) {
    const closeIcon = {uri:'data:image/png;base64,'};

    return (
        <View style={[styles.selectedItem, !props.enable && styles.disableColor]}>
            <Text style={[styles.labelText, !props.enable && styles.disableText || {}]}
                numberOfLines={1} >
                    {props.children}
            </Text>
            {props.enable && !props.readOnly && <TouchableHighlight
                style={styles.closeContainer}
                underlayColor="transparent"
                activeOpacity={0.5}
                onPress={props.onCancel}>
                <View>
                    <Image
                        style={styles.closeIcon}
                        source={closeIcon}
                        resizeMode="cover" />
                </View>
            </TouchableHighlight>
            }
        </View>
    );
}

export default LabelSelect;
const Color = {
    disableColor: '#eaeaea',
    main: '#40cca2'
};

const IMG = {
    closeIcon: 'data:image/png;base64,',
    addIcon: 'data:image/png;base64,'
};

const styles = StyleSheet.create({
    selectedView: {
        flexDirection: 'row',
        justifyContent: 'flex-start',
        alignItems: 'center',
        flexWrap: 'wrap'
    },
    selectedItem: {
        margin: 4,
        borderWidth: 2 / scale,
        borderRadius: 6,
        borderColor: '#aaa',
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        backgroundColor: '#f6f6f6'
    },
    addItem: {
        padding: 7
    },
    disableColor: {
        backgroundColor: Color.disableColor
    },
    labelText: {
        padding: 6,
        fontSize: 14,
        lineHeight: 14,
        maxWidth: 300
    },
    closeContainer: {
        padding: 8,
        borderLeftWidth: 2 / scale,
        borderLeftColor: '#c8c8c8'
    },
    closeIcon: {
        width: 10,
        height: 10
    },
    addIcon: {
        width: 12,
        height: 12
    },
    modalMask: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#00000077'
    },
    modalContainer: {},
    modal: {
        height: height * 0.6,
        width: width * 0.6,
        overflow: 'hidden',
        borderRadius: 10,
        backgroundColor: '#fff'
    },
    title: {
        paddingHorizontal: 20,
        paddingVertical: 10,
        borderBottomWidth: 2 / scale,
        borderBottomColor: '#bbb'
    },
    titleText: {
        fontSize: 18,
        lineHeight: 20
    },
    scrollView: {
        height: height * 0.6 - 80
    },
    buttonView: {
        borderBottomLeftRadius: 10,
        borderBottomRightRadius: 10,
        flexDirection: 'row',
        justifyContent: 'space-around'
    },
    modalButton: {
        height: 40,
        width: width * 0.3,
        paddingLeft: 20,
        paddingRight: 20,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: Color.main
    },
    modalItem: {
        height: 50,
        paddingHorizontal: 20,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        borderBottomWidth: 2 / scale,
        borderBottomColor: '#bbb'
    },
    modalText: {
        fontSize: 16,
        width: width * 0.6 - 70
    },
    buttonText: {
        color: '#fff',
        fontSize: 16
    },
    confirmButton: {
        borderLeftWidth: 2 / scale,
        borderLeftColor: '#fff'
    },
    outerCircle: {
        borderWidth: 2 / scale,
        borderColor: '#888',
        width: 20,
        height: 20,
        borderRadius: 10,
        overflow: 'hidden',
        justifyContent: 'center',
        alignItems: 'center'
    },
    enableCircle: {
        borderColor: Color.main
    },
    innerCircle: {
        backgroundColor: Color.main,
        width: 16,
        height: 16,
        borderRadius: 8,
        overflow: 'hidden'
    },
    disableText: {
        color: '#999'
    }
});
