const languagesReducer = async (state:any, action:any) => {
  if(!state){
    let all = await fetch("https://us-central1-mparablas.cloudfunctions.net/getAllLanguges",).then(data=>{return  data.json()})
    state = {
      allLanguges:all,
      userLanguages:[{
        title: "Hebrew",
        flag: "https://mparablas.web.app/images/iw.png"
        },
        {
        title: "English",
        flag: "https://mparablas.web.app/images/en.png"
        },
        {
        flag: "https://mparablas.web.app/images/eo.png",
        title: "Esperanto"
        }]
    };
  }
  switch (action.type) {
    default:
      return state
  }
};

export default languagesReducer;
