/**
 * Created by TinySymphony on 2017-01-03.
 */

import PropTypes from 'prop-types';
import React, { useReducer } from 'react';
import { View, Text, Image, Modal, ScrollView, TouchableHighlight, Dimensions, StyleSheet } from 'react-native';
import ModalItem from './pTransparentModal';
import PModal from './pTransparentModal';
import wordsReducer from '../store/reducers/wordsReducer'
const window = Dimensions.get('window');
const { width, height, scale } = window;

class LabelSelect extends React.Component<any, any> {
    addIcon = {
        uri: IMG.addIcon
    }
    static Label: typeof Label;
    static ModalItem: typeof ModalItem;
    words = useReducer(        wordsReducer,{});

    constructor(props: any) {
        super(props);
    }
    openModal(){

    }
    
    render() {
        return (
            <View style={[styles.selectedView, this.props.style]}>

                <LabelSelect.Label key="one" onCancel={() => { }}>one</LabelSelect.Label>
                <LabelSelect.Label key="two" onCancel={() => { }}>two</LabelSelect.Label>
                <LabelSelect.Label key="tre" onCancel={() => { }}>tre</LabelSelect.Label>
                
                
                {this.props.enable && !this.props.readOnly && this.props.enableAddBtn &&
                    <TouchableHighlight
                        style={[styles.selectedItem, styles.addItem]}
                        underlayColor="transparent"
                        onPress={this.openModal}>
                        <Image
                            style={styles.addIcon}
                            source={this.addIcon}
                            resizeMode="cover"
                        />
                    </TouchableHighlight>
                }
                <PModal ></PModal>
            </View>
        );
    }
}

class Label extends React.Component<any, any> {
    closeIcon = {
        uri: IMG.closeIcon
    }
    static propTypes = {
        onCancel: PropTypes.func,
        readOnly: PropTypes.bool,
        enable: PropTypes.bool
    }
    static defaultProps = {
        onCancel: () => { },
        enable: true,
        readOnly: false,
        customStyle: {}
    }
    constructor(props: Readonly<{}>) {
        super(props);
    }
    render() {
        const { enable, readOnly, onCancel, customStyle } = this.props;
        return (
            <View style={[styles.selectedItem, !enable && styles.disableColor]}>
                <Text style={[styles.labelText, !enable && styles.disableText, customStyle.text || {}]}
                    numberOfLines={1} >{this.props.children}</Text>
                {enable && !readOnly && <TouchableHighlight
                    style={styles.closeContainer}
                    underlayColor="transparent"
                    activeOpacity={0.5}
                    onPress={onCancel}>
                    <View>
                        <Image
                            style={styles.closeIcon}
                            source={this.closeIcon}
                            resizeMode="cover" />
                    </View>
                </TouchableHighlight>}
            </View>
        );
    }
}

LabelSelect.Label = Label;

export default LabelSelect;



const Color = {
    disableColor: '#eaeaea',
    main: '#40cca2'
};

const IMG = {
    closeIcon: 'data:image/png;base64,',
    addIcon: 'data:image/png;base64,'
};

const styles = StyleSheet.create({
    selectedView: {
        flexDirection: 'row',
        justifyContent: 'flex-start',
        alignItems: 'center',
        flexWrap: 'wrap'
    },
    selectedItem: {
        margin: 4,
        borderWidth: 2 / scale,
        borderRadius: 6,
        borderColor: '#aaa',
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
        backgroundColor: '#f6f6f6'
    },
    addItem: {
        padding: 7
    },
    disableColor: {
        backgroundColor: Color.disableColor
    },
    labelText: {
        padding: 6,
        fontSize: 14,
        lineHeight: 14,
        maxWidth: 300
    },
    closeContainer: {
        padding: 8,
        borderLeftWidth: 2 / scale,
        borderLeftColor: '#c8c8c8'
    },
    closeIcon: {
        width: 10,
        height: 10
    },
    addIcon: {
        width: 12,
        height: 12
    },
    modalMask: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#00000077'
    },
    modalContainer: {},
    modal: {
        height: height * 0.6,
        width: width * 0.6,
        overflow: 'hidden',
        borderRadius: 10,
        backgroundColor: '#fff'
    },
    title: {
        paddingHorizontal: 20,
        paddingVertical: 10,
        borderBottomWidth: 2 / scale,
        borderBottomColor: '#bbb'
    },
    titleText: {
        fontSize: 18,
        lineHeight: 20
    },
    scrollView: {
        height: height * 0.6 - 80
    },
    buttonView: {
        borderBottomLeftRadius: 10,
        borderBottomRightRadius: 10,
        flexDirection: 'row',
        justifyContent: 'space-around'
    },
    modalButton: {
        height: 40,
        width: width * 0.3,
        paddingLeft: 20,
        paddingRight: 20,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: Color.main
    },
    modalItem: {
        height: 50,
        paddingHorizontal: 20,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        borderBottomWidth: 2 / scale,
        borderBottomColor: '#bbb'
    },
    modalText: {
        fontSize: 16,
        width: width * 0.6 - 70
    },
    buttonText: {
        color: '#fff',
        fontSize: 16
    },
    confirmButton: {
        borderLeftWidth: 2 / scale,
        borderLeftColor: '#fff'
    },
    outerCircle: {
        borderWidth: 2 / scale,
        borderColor: '#888',
        width: 20,
        height: 20,
        borderRadius: 10,
        overflow: 'hidden',
        justifyContent: 'center',
        alignItems: 'center'
    },
    enableCircle: {
        borderColor: Color.main
    },
    innerCircle: {
        backgroundColor: Color.main,
        width: 16,
        height: 16,
        borderRadius: 8,
        overflow: 'hidden'
    },
    disableText: {
        color: '#999'
    }
});
