const languagesReducer = async (state:any, action:any) => {
  if(!state){
    let all = await fetch("https://us-central1-mparablas.cloudfunctions.net/getAllLanguges", {
      headers: {
        'Content-Type': 'application/json',
        'A': 'application/json',
    }
  }).then(data=> {
      debugger;
      return data.json();
    }).catch(e=>{
      debugger;
    });
    debugger
    state = {
      allLanguges:all,
      userLanguages:[{
        title: "Hebrew",
        flag: "https://mparablas.web.app/images/iw.png"
        },
        {
        title: "English",
        flag: "https://mparablas.web.app/images/en.png"
        },
        {
        flag: "https://mparablas.web.app/images/eo.png",
        title: "Esperanto"
        }]
    };
  }
  switch (action.type) {
    default:
      return state
  }
};

export default languagesReducer;
